import React, { useState } from "react";
import {
  TouchableOpacity,
  Text,
  View,
  SafeAreaView,
  StatusBar,
  FlatList,
  Modal,
  TextInput,
  ScrollView
} from "react-native";
import styles from "../style/index";
import { Ionicons } from "@expo/vector-icons";
import States from "../components/state";
import TaskList from "../components/taskList";
import * as Animatable from "react-native-animatable";

const ButtonAnimated = Animatable.createAnimatableComponent(TouchableOpacity);

export default function App() {
  const [task, setTask] = useState([
    { key: 1, task: "Comprar p√£o" },
    { key: 2, task: "Fazer dever de casa" },
    { key: 3, task: "Estudar react native" },
    { key: 4, task: "Quimica" },
    { key: 5, task: "Jujuba" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
    { key: 6, task: "Gersin" },
  ]);
  const [backgroud, setBackgroud] = useState("black");
  const [open, setOpen] = useState(false);
  return (
    <SafeAreaView style={styles.container}>
      <StatusBar backgroundColor="#111" barStyle="light-content" />
      <View>
        <Animatable.Text
          animation="flipInY"
          useNativeDriver
          style={styles.title}
        >
          My Tasks
        </Animatable.Text>
        <FlatList
          style={styles.flatList}
          showsHorizontalScrollIndicator={false}
          data={task}
          keyExtractor={(item) => String(item.key)}
          renderItem={({ item }) => <TaskList data={item} />} //desativate the scroll horizontal
        />
      </View>
      <Modal animationType="slide" transparent={false} visible={open}>
        <SafeAreaView style={styles.container}>
          <View style={styles.modalHeader}>
            <TouchableOpacity
              style={styles.buttonReturn}
              onPress={() => setOpen(!open)}
            >
              <Ionicons
                style={styles.buttonReturnIcon}
                name="md-arrow-back"
                color={"#fff"}
                size={50}
              />
            </TouchableOpacity>
            <Text style={styles.modalHeaderTitle}>New Task</Text>
          </View>
          <View style={styles.modalBody}>
            <Animatable.Text
              style={styles.modalTitle}
              animation="bounceInUp"
              useNativeDriver
              duration={2000}
            >
              Whats will you do today ?
            </Animatable.Text>
            <TextInput placeholder="Ol√°" style={styles.modalInput} />
          </View>
        </SafeAreaView>
      </Modal>
      <ButtonAnimated
        style={styles.buttonAdd}
        animation="bounceInUp"
        useNativeDriver
        duration={2000}
        onPress={() => setOpen(!open)}
      >
        <Ionicons style={styles.icon} name="add" color={"#121212"} size={50} />
      </ButtonAnimated>
    </SafeAreaView>
  );
}
